import React, { useEffect, useState } from "react";
import "./App.css";
import { MenuItem, Select, FormControl } from "@mui/material";

const App = () => {
  const [countries, setCountries] = useState(["USA", "Canada", "Mexico"]);
  useEffect(() => {
    const getCountriesData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
        .then((res) => res.json())
        .then((data) => {
          const countries = data.map((country) => ({
            name: country.country,
            value: country.countryInfo2,
          }));

          setCountries(countries);
        });
    };

    getCountriesData();
  }, []); // runs functions once, and then once more each time country changes

  return (
    <div className="App">
      <div className="app_header">
        <h1>Disease Tracker</h1>

        <FormControl className="app_dropdown">
          <Select variant="outlined" value="abc">
            {/* Map countires to the drop down */}
            {countries.map((country) => (
              <MenuItem value={country.value}>{country.name}</MenuItem>
            ))}

            {/* <MenuItem value="worldwide">worldwide</MenuItem>
            <MenuItem value="worldwide">worldwide</MenuItem>
            <MenuItem value="worldfdsfsdwide">worldwfdfsfdide</MenuItem> */}
          </Select>
        </FormControl>
      </div>
    </div>
  );
};

export default App;
